import React, { useEffect, useRef, useState } from 'react';
import { Dropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import FeatherIcon from 'feather-icons-react';

// components
import Tagline from './Tagline';
import NavBar from './NavBar';
import Hero from './Hero';
import Footer from './Footer';
import profileImg from '../assets/images/users/avatar-1.jpg';

const Landing = () => {

    useEffect(() => {
        if (document.body) {
            document.body.classList.remove('authentication-bg');
            document.body.classList.add('pb-0');
        }

        return () => {
            if (document.body) document.body.classList.remove('pb-0');
        };
    }, []);

    const ProfileMenus = [
        {
            label: 'My Account',
            icon: 'user',
            redirectTo: '/pages/profile',
        },
        {
            label: 'Settings',
            icon: 'settings',
            redirectTo: '/',
        },
        {
            label: 'Support',
            icon: 'help-circle',
            redirectTo: '/',
        },
        {
            label: 'Lock Screen',
            icon: 'lock',
            redirectTo: '/auth/lock-screen',
        },
        {
            label: 'Logout',
            icon: 'log-out',
            redirectTo: '/auth/logout',
        },
    ];

    const [dropdownOpen, setDropdownOpen] = useState<boolean>(false);

    /*
     * toggle dropdown
     */
    const toggleDropdown = () => {
        setDropdownOpen(!dropdownOpen);
    };

    return (
        <div className="landing">
            {/* tagline */}

            <div className="user-box text-center">
            <img src={profileImg} alt="" title="Mat Helme" className="rounded-circle avatar-md" />
            <Dropdown show={dropdownOpen} onToggle={toggleDropdown}>
                <Dropdown.Toggle
                    id="dropdown-notification"
                    as="a"
                    onClick={toggleDropdown}
                    className="cursor-pointer text-dark h5 mt-2 mb-1 d-block">
                    Nik Patel
                </Dropdown.Toggle>
                <Dropdown.Menu className="user-pro-dropdown m-0">
                    {/* <div onClick={toggleDropdown}> */}
                    {(ProfileMenus || []).map((item, index) => {
                        return (
                            <React.Fragment key={index + '-profile-menu'}>
                                {index === ProfileMenus.length - 1 && <div className="dropdown-divider"></div>}
                                <Link to={item.redirectTo} className="dropdown-item notify-item">
                                    <FeatherIcon icon={item.icon} className="icon-dual icon-xs me-1" />
                                    <span>{item.label}</span>
                                </Link>
                            </React.Fragment>
                        );
                    })}
                    {/* </div> */}
                </Dropdown.Menu>
            </Dropdown>
            <p className="text-muted">Admin Head</p>
        </div>

            <Tagline />

            {/* navbar */}
            <NavBar />

            {/* hero */}
            <Hero />

            {/* footer */}
            <Footer />
        </div>
    );
};

export default Landing;
